import './Item.css';
import { useState } from 'react'
const listItems = [
    {
        id: "T1",
        item:"Test  1",
        value: 100
    },
    {
        id: "T2",
        item:"Test  1",
        value: 50
    },
    {
        id: "T3",
        item:"Test  1",
        value:70
    },
    {
        id: "T4",
        item:"Test  1",
        value:10
    },
    {
        id: "T5",
        item:"Test  1",
        value: 80
    }

];
const Item = (props) => {
    const[priceperunit, setpriceperunit]  = useState('100');
    const[Amount, setAmount]  = useState(0);
    const QyantityHandler =(event) =>
    {
        if(event.target.value === '')
        {
            setAmount(0);
            return;
        }
        if(validateNumeric(event.target.value))
        {
            alert("Insert numeric value");
            return;
        }
        setAmount(priceperunit * event.target.value);
        props.ptotalAmount(Amount);
    }
    const validateNumeric=(value)=>
    {
        const re = /^[0-9\b]+$/;
        if (re.test(value)) {
            return false;
         }
        return true;
    }

    return (
        <div className='itm-margin-2'>

            <div className='itm-width itm-float-left itm-textalign-center'>
                <select className='itm-select1-width'>
                    {listItems.map(items => <option value={items.id}>{items.item}</option>)}
                    {/* <option value="a">A</option>
                    <option value="b">B</option> */}
                </select>
                {/* <input className="itm-input" type="text" name="item"></input> */}
            </div>
            <div className='itm-width itm-float-left itm-textalign-center'>
                <input onChange={QyantityHandler} className="itm-input" type="text" name="Quantity"></input>
            </div>
            <div className='itm-width itm-float-left itm-textalign-center'>
                <label className="itm-label">{priceperunit}</label>
            </div>
            <div className='itm-width itm-float-left itm-textalign-center'>
                <label className="itm-label"></label>
            </div>
            <div className='itm-width itm-float-left itm-textalign-center'>
                <label className="itm-label">{Amount}</label>
            </div>
        </div>
    );
}
export default Item;
