import './invoice.css';
import { useState } from 'react'
import Item from './Item'
import AddIcon from '../../../images/Add-button.png'

const mylists = [
    {abc : "123"},
    {abc : "sohit"}
];
var mylist = [];
const Invoice = () => {  
    const [mylist, setmylists] = useState(mylists);
    // const mylist = [
    //     1,2,3
    // ]
    const AddRowHandler = () =>
    {
        const newitem = {abc : "123"};
        setmylists((prevmylist) =>{
           return [...prevmylist, newitem ];
        }
        );

    }
    return (
        <div className="inv-content">
            <div className="inv-wdthgt inv-border inv-float ">
                <span><label className="inv-label">Invoice</label></span>
            </div>
            <div className='inv-margin'>
                <div className='inv-width inv-float-left inv-textalign-center'>
                    <label className="inv-label">Item</label>
                </div>
                <div className='inv-width inv-float-left inv-textalign-center'>
                    <label className="inv-label">Qunatity</label>
                </div>
                <div className='inv-width inv-float-left inv-textalign-center'>
                    <label className="inv-label">PricePerUnit</label>
                </div>
                <div className='inv-width inv-float-left inv-textalign-center'>
                    <label className="inv-label">Amount</label>
                </div>
                <div className='inv-width inv-float-left inv-textalign-center'>
                    <img alt="addimage" onClick={AddRowHandler} className="inv-img" src={AddIcon}></img>
                </div>
            </div>
            <div className='inv-height-items'>
                {mylist.map(<Item/>)}
            </div>       
        </div>
    );
}
export default Invoice;
