import './Item.css';
import { useState } from 'react'

const blank = "_";

const listItems = [
    {
        id: "T1",
        item:"Test  2",
        value: 100
    },
    {
        id: "T2",
        item:"Test  2",
        value: 50
    },
    {
        id: "T3",
        item:"Test  3",
        value:70
    },
    {
        id: "T4",
        item:"Test  4",
        value:10
    },
    {
        id: "T5",
        item:"Test  5",
        value: 80
    }

];
var arraydata = [

];
const Item = (props) => {
    const [stotalAmount, setstotalAmount] = useState(arraydata);
    const[priceperunit, setpriceperunit]  = useState('100');
    const[Amount, setAmount]  = useState(0);
    const[Quantity, setQuantity] = useState(0);
    const[MenuItems, setMenuItems] = useState(()=>
    {
        // We will write fetch api to get the data console.log("initial state");
        setMenuItems(listItems);
    });
    const QyantityHandler =(event) =>
    {
        setQuantity(0);
        if(event.target.value === '')
        {
            setAmount(0);
            return;
        }
        if(validateNumeric(event.target.value))
        {
            alert("Insert numeric value");
            return;
        }
        setQuantity(event.target.value);
        setAmount(priceperunit * event.target.value);
        props.onQuantityChange(priceperunit * event.target.value , props.itemsId);
    }
    const validateNumeric=(value)=>
    {
        const re = /^[0-9\b]+$/;
        if (re.test(value)) {
            return false;
         }
        return true;
    }
    const selectItemHandler=(event)=>
    {
        setpriceperunit(event.target.value.split("_")[1]);
        setAmount(event.target.value.split("_")[1] * Quantity);
        props.onQuantityChange(event.target.value.split("_")[1] * Quantity , props.itemsId);
    }

    return (
        <div className='itm-margin-2'>

            <div className='itm-width itm-float-left itm-textalign-center'>
                <select onChange={selectItemHandler} className='itm-select1-width'>
                    {MenuItems.map(items => <option value={items.id + "_" + items.value}>{items.item}</option>)}
                    {/* <option value="a">A</option>
                    <option value="b">B</option> */}
                </select>
                {/* <input className="itm-input" type="text" name="item"></input> */}
            </div>
            <div className='itm-width itm-float-left itm-textalign-left'>
                <input onChange={QyantityHandler} className="itm-input" type="text" name="Quantity"></input>
            </div>
            <div className='itm-width itm-float-left itm-textalign-left'>
                <label className="itm-label">{priceperunit}</label>
            </div>
            <div className='itm-width itm-float-left itm-textalign-left'>
                <label className="itm-label">{Amount}</label>
            </div>
            <div className='itm-width itm-float-left itm-textalign-center'>

            </div>
        </div>
    );
}
export default Item;
