import './invoice.css';
import { useState } from 'react'
import Item from './Item'
import AddIcon from '../../../images/Add-button.png'

const mylists = [
];
const Invoice = () => {
    const [mylist, setmylists] = useState(mylists);
    const [Total, setTotal] = useState(0);
    const AddRowHandler = () => {
        // mylist.concat({
        //     id:Date.now(),
        //     amount:""
        // });
        console.log(mylist.map(values => values.amount));
        setmylists((prevmylist) => {
            return (prevmylist.concat({ id: Date.now(), amount: "", ...prevmylist }));
        }
        );
        // console.log(mylist.map(value=> value.amount));

    }
    const getTotalAmountHandler = () => {

    }
    const onQuantityChangeHandler = (value, id) => {
        console.log("onqualitychangehandler");
        console.log("value : "+  value);
        console.log("Id : "+ id);
        const newData = mylist.map(data => {

            if (data.id !== id) {
                return data;
            }
            return {
                ...data, amount: value
            };


        });
        setmylists(newData);
        const sum = newData.map(values => values.amount).reduce((result, number) => result + number);
        setTotal(sum);
        console.log("Total : "+Total);
        console.log("Sum : "+sum);
    }
    const onItemChangeHandler = () => {

    }
    return (
        <div className="inv-content">
            <div className="inv-wdthgt inv-border inv-float ">
                <span><label className="inv-label">Invoice</label></span>
            </div>
            <div className='inv-margin'>
                <div className='inv-width inv-float-left inv-textalign-center'>
                    <label className="inv-label">Item</label>
                </div>
                <div className='inv-width inv-float-left inv-textalign-center'>
                    <label className="inv-label">Qunatity</label>
                </div>
                <div className='inv-width inv-float-left inv-textalign-center'>
                    <label className="inv-label">PricePerUnit</label>
                </div>
                <div className='inv-width inv-float-left inv-textalign-center'>
                    <label className="inv-label">Amount</label>
                </div>
                <div className='inv-width inv-float-left inv-textalign-center'>
                    <img alt="addimage" onClick={AddRowHandler} className="inv-img" src={AddIcon}></img>
                </div>
            </div>
            <div className='inv-height-items'>
                {mylist.map(items => <Item onQuantityChange={onQuantityChangeHandler} itemsId={items.id} onItemChange={onItemChangeHandler} ptotalAmount={getTotalAmountHandler} key={items.id} />)}
            </div>
            <div className='inv-height-total'>
                <label >Total:</label>
                <label className="inv-float-right" >{Total}</label>
            </div>
        </div>
    );
}
export default Invoice;
