import './Registeration.css';

function SignIn(props) {

    const submitHandler = (event) => {
        if(ValidateIsEmpty(emailState))
        {
            alert("Email : Can not be blank");
            return;
        }
        if(ValidateIsEmpty(passState))
        {
            alert("Password : Can not be blank");
            return;
        }
            

        const data = {
            "Email": emailState,
            "Password": passState
        };
        fetch("https://localhost:44389/user/userdetails?psUserName=sohit.chaudhary@gmail.com",
            {
                method: 'POST',
                body: JSON.stringify(data),
                headers:{
                    'Content-Type' : "application/json"
                }           
            }).then((response) => {
                if (response.status != 200) {
                    alert("Something went wrong");
                    console.log("Error : " + response.status);
                    return;
                }
                response.json().then((data) => {
                    setEmailState("");
                    setPassState("");
                    props.IsRegister("Signin");
                });
            });
            
    }
    return (
        <form className="form">
            <div className="container">
                <ul className="formsection">
                    <li>
                        <div className="divflex" >
                            <span><h1>Sign In</h1></span>
                        </div>

                    </li>
                    <li>
                        <div className="lidiv">
                            <input type="text" placeholder="Enter Email" name="email" id="email" required></input>
                        </div>
                    </li>
                    <li>
                    <div className="lidiv">
                            <input type="password" placeholder="Enter Password" name="psw" id="psw" required></input>
                        </div>
                    </li>
                    <li>
                        <div className ="divflex">
                            <input type="button" onClick={submitHandler} value="Sign In" ></input>
                        </div>
                    </li>
                </ul>
            </div>
        </form>
    );
}
export default SignIn;
